import React, { useState, useRef } from 'react';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { copyText } from './../../utils/copy';
const CSSOutputWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Title = styled.h1``;

const CSSOutputContent = styled.div`
  border: 1px solid white;
  margin: 10px;
  text-align: left;
  padding: 50px 20px;
  position: relative;
`;

const Copy = styled.div`
  position: absolute;
  top: 10px;
  right: 10px;
  cursor: pointer;
`;

const CssOutput = ({ cssProps }) => {
  const stringifyCSS = cssProps => {
    let copiedCSS = '';
    Object.keys(cssProps).forEach(cssProp => {
      copiedCSS += `${cssProp}: ${cssProps[cssProp]}; \n`;
    });
    return copiedCSS;
  };
  const domEl = useRef(null);
  const [copyClicked, setCopyClicked] = useState(false);

  const onClicked = () => {
    setCopyClicked(true);
    debugger;
    domEl.current.select();
    document.execCommand('copy');

    // copyText(stringifyCSS(cssProps));
  };

  return (
    <CSSOutputWrapper>
      <Title>CSS Output</Title>
      <CSSOutputContent>
        <Copy
          onClick={() => {
            onClicked();
          }}
        >
          <FontAwesomeIcon icon="copy" />
        </Copy>
        <textarea ref={domEl} selectable>
          {Object.keys(cssProps).map(cssProp => ` ${cssProp} : ${cssProps[cssProp]};`)}
        </textarea>
      </CSSOutputContent>
    </CSSOutputWrapper>
  );
};

export default CssOutput;
