export const copyText = text => {
  let data = new DataTransfer();

  data.items.add('text/plain', text);
  navigator.clipboard.writeText(data).then(
    function() {
      debugger;
    },
    function(a, b, c) {
      debugger;
      /* clipboard write failed */
    },
  );
};
