import React, { useState, useEffect } from 'react';

export const copy = text => {
  debugger;
  navigator.clipboard.writeText(text).then(
    function() {
      setCopySuccess('Copied!');
    },
    function() {
      /* clipboard write failed */
    },
  );
};
